import { injectable } from 'inversify';
import { app, BrowserWindow, nativeImage, TouchBar } from 'electron';
import { ElectronMainApplication, TheiaBrowserWindowOptions } from '@theia/core/lib/electron-main/electron-main-application';
import { isOSX, MaybePromise } from '@theia/core';
import { VesRunCommand, VesSelectEmulatorCommand } from '../browser/run/commands';
import { VesBuildCleanCommand, VesBuildCommand, VesBuildExportCommand, VesBuildSetModeBetaCommand, VesBuildSetModeDebugCommand, VesBuildSetModePreprocessorCommand, VesBuildSetModeReleaseCommand, VesBuildSetModeToolsCommand } from '../browser/build/commands';
import { VesFlashCartsCommand } from '../browser/flash-carts/commands';
import { BuildMode } from '../browser/build/types';

@injectable()
export class VesElectronMainApplication extends ElectronMainApplication {
    async createWindow(asyncOptions: MaybePromise<TheiaBrowserWindowOptions> = this.getDefaultBrowserWindowOptions()): Promise<BrowserWindow> {
        const electronWindow = await super.createWindow(asyncOptions);
        // electronWindow.on('focus', () => electronWindow.setOpacity(1));
        // electronWindow.on('blur', () => electronWindow.setOpacity(0.95));
        this.registerVesTouchBar(electronWindow);
        return electronWindow;
    }

    protected async getDefaultBrowserWindowOptions(): Promise<TheiaBrowserWindowOptions> {
        return {
            ...await super.getDefaultBrowserWindowOptions(),
            backgroundColor: "#222",
            frame: isOSX,
            minHeight: 560,
            minWidth: 820,
            titleBarStyle: "hiddenInset"
        }
    }

    protected registerVesTouchBar(electronWindow: BrowserWindow) {
        const { TouchBarButton, TouchBarLabel, TouchBarPopover, TouchBarSegmentedControl } = TouchBar;

        const vesIcon = nativeImage.createFromDataURL("data:image/png;base64,").resize({
            height: 16,
        });
        const cleanIcon = nativeImage.createFromDataURL("data:image/png;base64,").resize({
            height: 16,
        });
        const buildIcon = nativeImage.createFromDataURL("data:image/png;base64,").resize({
            height: 16,
        });
        const runIcon = nativeImage.createFromDataURL("data:image/png;base64,").resize({
            height: 16,
        });
        const flashIcon = nativeImage.createFromDataURL("data:image/png;base64,").resize({
            height: 18,
        });
        const exportIcon = nativeImage.createFromDataURL("data:image/png;base64,").resize({
            height: 16,
        });
        const queuedIcon = nativeImage.createFromDataURL("data:image/png;base64,").resize({
            height: 16,
        });

        let spinnerIconFrame = 0;
        let spinnerIconIntervall: NodeJS.Timer | null = null;
        const spinnerIconSize = 18;
        const spinnerIcon = [
            nativeImage.createFromDataURL("data:image/png;base64,").resize({ height: spinnerIconSize }),
            nativeImage.createFromDataURL("data:image/png;base64,").resize({ height: spinnerIconSize }),
            nativeImage.createFromDataURL("data:image/png;base64,").resize({ height: spinnerIconSize }),
            nativeImage.createFromDataURL("data:image/png;base64,").resize({ height: spinnerIconSize }),
            nativeImage.createFromDataURL("data:image/png;base64,").resize({ height: spinnerIconSize }),
            nativeImage.createFromDataURL("data:image/png;base64,").resize({ height: spinnerIconSize }),
            nativeImage.createFromDataURL("data:image/png;base64,").resize({ height: spinnerIconSize }),
            nativeImage.createFromDataURL("data:image/png;base64,").resize({ height: spinnerIconSize }),
        ];

        const vesButton = new TouchBarButton({
            backgroundColor: "#3b2576",
            icon: vesIcon,
            click: () => app.emit("ves-execute-command", "workbench.action.showCommands"),
        });

        const buildMenuCleanButton = new TouchBarButton({
            icon: cleanIcon,
            accessibilityLabel: VesBuildCleanCommand.id,
        });
        const buildMenuBuildButton = new TouchBarButton({
            icon: buildIcon,
            accessibilityLabel: VesBuildCommand.id,
        });
        const buildMenuRunButton = new TouchBarButton({
            icon: runIcon,
            accessibilityLabel: VesRunCommand.id,
        });
        const buildMenuFlashButton = new TouchBarButton({
            icon: flashIcon,
            accessibilityLabel: VesFlashCartsCommand.id,
        });
        const buildMenuExportButton = new TouchBarButton({
            icon: exportIcon,
            accessibilityLabel: VesBuildExportCommand.id,
        });

        const buildMenuSegmentedControlSegments = [
            buildMenuCleanButton,
            buildMenuBuildButton,
            buildMenuRunButton,
            buildMenuFlashButton,
            buildMenuExportButton,
        ];

        const buildMenuSegmentedControl = new TouchBarSegmentedControl({
            segmentStyle: 'automatic',
            selectedIndex: 0,
            mode: 'buttons',
            segments: buildMenuSegmentedControlSegments,
            change: (selectedIndex) => app.emit("ves-execute-command", buildMenuSegmentedControlSegments[selectedIndex].accessibilityLabel),
        });

        const buildModes = [{
            label: this.capitalizeFirstLetter(BuildMode.release),
            accessibilityLabel: VesBuildSetModeReleaseCommand.id,
        }, {
            label: this.capitalizeFirstLetter(BuildMode.beta),
            accessibilityLabel: VesBuildSetModeBetaCommand.id,
        }, {
            label: this.capitalizeFirstLetter(BuildMode.tools),
            accessibilityLabel: VesBuildSetModeToolsCommand.id,
        }, {
            label: this.capitalizeFirstLetter(BuildMode.debug),
            accessibilityLabel: VesBuildSetModeDebugCommand.id,
        }, {
            label: this.capitalizeFirstLetter(BuildMode.preprocessor),
            accessibilityLabel: VesBuildSetModePreprocessorCommand.id,
        }];

        const buildModeButtonSegmentedControl = new TouchBarSegmentedControl({
            segmentStyle: 'automatic',
            mode: 'single',
            segments: buildModes,
            selectedIndex: 1,
            change: (selectedIndex) => app.emit("ves-execute-command", buildModes[selectedIndex].accessibilityLabel),
        });

        const buildModeButton = new TouchBarPopover({
            label: buildModes[buildModeButtonSegmentedControl.selectedIndex].label,
            showCloseButton: true,
            items: new TouchBar({
                items: [
                    new TouchBarLabel({ label: 'Build Mode:' }),
                    buildModeButtonSegmentedControl
                ]
            }),
        });

        const emulatorButton = new TouchBarButton({
            label: "Mednafen (2D)",
            click: () => app.emit("ves-execute-command", VesSelectEmulatorCommand.id),
        });

        const vesTouchBar = new TouchBar({
            items: [
                vesButton,
                buildMenuSegmentedControl,
                buildModeButton,
                emulatorButton,
            ]
        });

        electronWindow.setTouchBar(vesTouchBar);

        // @ts-ignore
        app.on("ves-change-build-mode", (buildMode: string) => {
            buildModeButton.label = this.capitalizeFirstLetter(buildMode);
            buildModeButtonSegmentedControl.selectedIndex =
                (buildMode === BuildMode.release) ? 0 :
                    (buildMode === BuildMode.beta) ? 1 :
                        (buildMode === BuildMode.tools) ? 2 :
                            (buildMode === BuildMode.debug) ? 3 :
                                (buildMode === BuildMode.preprocessor) ? 4 :
                                    0;
        });
        // @ts-ignore
        app.on("ves-change-is-building", (isBuilding: boolean) => {
            if (isBuilding) {
                spinnerIconIntervall = setInterval(animateSpinner, 130);
            } else {
                if (spinnerIconIntervall) clearInterval(spinnerIconIntervall);
                buildMenuBuildButton.icon = buildIcon;
                redrawMenuSegmentedControl();
            }
        });
        // @ts-ignore
        app.on("ves-change-is-run-queued", (isQueued: boolean) => {
            buildMenuRunButton.icon = isQueued ? queuedIcon : runIcon;
            redrawMenuSegmentedControl();
        });
        // @ts-ignore
        app.on("ves-change-is-flash-queued", (isQueued: boolean) => {
            buildMenuFlashButton.icon = isQueued ? queuedIcon : flashIcon;
            redrawMenuSegmentedControl();
        });
        // @ts-ignore
        app.on("ves-change-is-export-queued", (isQueued: boolean) => {
            buildMenuExportButton.icon = isQueued ? queuedIcon : exportIcon;
            redrawMenuSegmentedControl();
        });
        // @ts-ignore
        app.on("ves-change-connected-flash-cart", (flashCartConfig) => {
            buildMenuFlashButton.enabled = !!flashCartConfig;
            redrawMenuSegmentedControl();
        });
        // @ts-ignore
        app.on("ves-change-build-folder", (flags) => {
            const buildMode = buildModes[buildModeButtonSegmentedControl.selectedIndex].label.toLowerCase();
            buildMenuCleanButton.enabled = flags[buildMode];
            redrawMenuSegmentedControl();
        });
        // @ts-ignore
        app.on("ves-change-emulator", (name) => {
            emulatorButton.label = this.shorten(name, 20);
        });

        const animateSpinner = () => {
            spinnerIconFrame++;
            if (spinnerIconFrame >= spinnerIcon.length) spinnerIconFrame = 0;
            buildMenuBuildButton.icon = spinnerIcon[spinnerIconFrame];
            redrawMenuSegmentedControl();
        };

        const redrawMenuSegmentedControl = () => {
            buildMenuSegmentedControl.selectedIndex = 0; // have to update element somehow to trigger redrawing of its children
        };
    }

    private capitalizeFirstLetter(word: string) {
        return word.charAt(0).toUpperCase() + word.slice(1);
    }

    private shorten(word: string, length: number) {
        if (word.length <= length) return word;

        return word.slice(0, length) + "";
    }
}
